				const frontmatter$p = {"title":"Form layout","description":"Form Layout offers structured classes for creating accessible, consistent, and responsive form layouts.","since":"2024.07.1","new":true,"template":"default","layout":"/home/runner/work/envision/envision/packages/envision-docs/src/templates/Default.astro"};
