				const frontmatter$f = {"title":"Dynamic font size","description":"The Dynamic Font Size utility scales an element’s base font size based on container width.","since":"2024.02.1","template":"default","layout":"/home/runner/work/envision/envision/packages/envision-docs/src/templates/Default.astro"};
